import { Modal } from "bootstrap";

class ConfirmModal {
  constructor() {
    window.Turbo.setConfirmMethod(this.confirm.bind(this));
    this.element = document.querySelector("#confirm-modal");
  }

  confirm(message, trigger) {
    // When using Turbo, trigger can be a form autogenerated, not the source link 
    // If it is the case we try to find the source link to get the list of data attributes
    const pathName = new URL(trigger.getAttribute("action")).pathname;
    const sourceLink = trigger.getAttribute("data-turbo-confirm-text-content") ? trigger : document.querySelector(`a[href='${pathName}'][data-turbo-confirm='${message}']`);

    this.element.querySelector(".modal-title").textContent = message;
    this.element.querySelector("#custom-body").innerHTML = sourceLink.getAttribute("data-turbo-confirm-text-content") || message;
    this.element.querySelector("#confirm-button").innerHTML = sourceLink.getAttribute("data-turbo-confirm-text-action") || "Confirmer";

    this.modal = new Modal(this.element);
    this.modal.show();

    return new Promise((resolve) => {
      this.element.querySelector("#confirm-button").addEventListener("click", () => {
        resolve(true);
      });
    });
  }
}

export default ConfirmModal;